<script lang="ts">
import { VNode } from 'vue';
import { Component, Prop, Vue } from 'vue-property-decorator';

@Component
export default class extends Vue {
  // --------------------------------------------------------------------------
  // Fields
  // --------------------------------------------------------------------------

  @Prop({ type: String })
  public title!: string;

  @Prop({ type: String })
  public description!: string;

  @Prop({ type: String })
  public code?: string;

  @Prop({ type: Boolean, default: true })
  public codeEnabled!: boolean;

  private isCodeVisible = false;
  private codeHtml = '';

  // --------------------------------------------------------------------------
  // Methods
  // --------------------------------------------------------------------------

  private mounted() {
    const { highlightAuto } = require('highlight.js');
    const pretty = require('pretty');

    this.codeHtml = highlightAuto(pretty(unescape(this.code ?? ''))).value;
  }

  private toggleCodeVisibility() {
    this.isCodeVisible = !this.isCodeVisible;
  }
}
</script>

<template>
  <div class="grid grid-flow-col grid-cols-12 gap-8">
    <div class="[ col-span-3 ]">
      <div class="font-bold text-2xl">{{title}}</div>
      <div class="mt-2">{{description}}</div>
    </div>
    <div class="[ flex col-span-9 ]">
      <div
        ref="component"
        style="background: url(data:image/png;base64,)"
        class="flex flex-col justify-between flex-1 bg-gray-400 border border-gray-600 rounded-lg p-8 overflow-y-auto"
      >
        <div class="[ flex flex-1 flex-col ]">
          <slot />
        </div>
        <div v-if="codeEnabled" class="mt-8 flex flex-col">
          <a
            class="ml-auto -mb-5 block font-semibold pb-4 relative text-blue-600 text-sm"
            href="javascript:void"
            @click="toggleCodeVisibility()"
            v-html="`${isCodeVisible?'Hide': 'Show'} Code`"
          />
          <pre v-if="isCodeVisible" class="bg-gray-300 font-monospace mt-4 p-4 rounded font-bold"><code v-html="codeHtml" /></pre>
        </div>
      </div>
    </div>
  </div>
</template>
